mapper.select:
select ss_id,ss_name,ss_enactingPerson,ss_registrant,ss_registrationTime,ss_aduitStatus,ss_baseSalary,ss_trafficSubsidy,ss_communicationSubsidy,ss_floatWages,ss_routine,ss_lunchAllowance,ss_vehicleSubsidy 
from salarystandard

    ss_aduitStatus = 0

        and ss_name like "%"#{ss_name}"%"


        and ss_enactingPerson like "%"#{ss_enactingPerson}"%"


        and ss_registrant like "%"#{ss_registrant}"%"


        and ss_registrationTime >= #{ss_registrationTimeStart}


        and ss_registrationTime <= #{ss_registrationTimeEnd}


limit #{page},#{pageSize}

mapper.update:
update salarystandard set ss_name = #{ss_name},ss_enactingPerson = #{ss_enactingPerson},ss_registrant = #{ss_registrant},
ss_reviewer = #{ss_reviewer},ss_aduitOpinion = #{ss_aduitOpinion},ss_reviewTime = now(),ss_aduitStatus = #{ss_aduitStatus},
ss_baseSalary = #{ss_baseSalary},ss_communicationSubsidy = #{ss_communicationSubsidy},ss_floatWages = #{ss_floatWages},ss_routine = #{ss_routine},
ss_lunchAllowance = #{ss_lunchAllowance},ss_trafficSubsidy = #{ss_trafficSubsidy},ss_vehicleSubsidy = #{ss_vehicleSubsidy}
where ss_id = #{ss_id}

mapper.select:
select ss_id,ss_name,ss_enactingPerson,ss_registrant,ss_registrationTime,ss_reviewer,ss_reviewTime,ss_aduitStatus,ss_aduitOpinion,ss_baseSalary,ss_trafficSubsidy,ss_communicationSubsidy,ss_floatWages,ss_routine,ss_lunchAllowance,ss_vehicleSubsidy 
from salarystandard

    ss_aduitStatus = 1

        and ss_name like "%"#{ss_name}"%"


        and ss_enactingPerson like "%"#{ss_enactingPerson}"%"


        and ss_registrant like "%"#{ss_registrant}"%"


        and ss_reviewer like "%"#{ss_reviewer}"%"


        and ss_registrationTime >= #{ss_registrationTimeStart}


        and ss_registrationTime <= #{ss_registrationTimeEnd}


        and ss_reviewTime >= #{ss_reviewTimeStart}


        and ss_reviewTime <= #{ss_reviewTimeEnd}


limit #{page},#{pageSize}

select count(1)
from salarystandard

    ss_aduitStatus = 0

        and ss_name like "%"#{ss_name}"%"


        and ss_enactingPerson like "%"#{ss_enactingPerson}"%"


        and ss_registrant like "%"#{ss_registrant}"%"


        and ss_reviewer like "%"#{ss_reviewer}"%"


        and ss_registrationTime >= #{ss_registrationTimeStart}


        and ss_registrationTime <= #{ss_registrationTimeEnd}


        and ss_reviewTime >= #{ss_reviewTimeStart}


        and ss_reviewTime <= #{ss_reviewTimeEnd}
